[[[
if (entity.state == 0) {
return 'rgba(var(--color-theme),0.05)';
}
else {
return 'rgba(var(--color-' + variables.ulm_custom_card_yagrasdemonde_lights_count_color + '),0.2)';
}
]]]
grid:
